mapper.select:
/* BroadcastMapper.getBroadcastList */ SELECT ROW_NUMBER() OVER (ORDER BY A.mod_dt IS NULL ASC, A.mod_dt DESC, A.reg_dt DESC) AS row_num ,A.brdcast_no ,A.brdcast_nm ,A.district_no ,B.district_nm ,A.brdcast_svr_ip ,A.brdcast_svr_port ,A.brdcast_conn_id ,A.brdcast_conn_pwd ,TO_CHAR(TO_DATE(A.inst_ymd, 'YYYYMMDD'), 'YYYY-MM-DD') AS inst_ymd ,A.maint_sts_cd ,C.code_nm AS maint_sts_nm ,A.brdcast_lat ,A.brdcast_lon ,A.model_nm ,A.brdcast_maker ,TO_CHAR(A.reg_dt, 'YYYY-MM-DD HH24:MI:SS') AS reg_dt ,TO_CHAR(A.mod_dt, 'YYYY-MM-DD HH24:MI:SS') AS mod_dt FROM tb_broadcast_info A LEFT OUTER JOIN tb_district_info B ON A.district_no = B.district_no LEFT OUTER JOIN (select * from tb_common_code where code_grp_id = 'MTN000') C on A.maint_sts_cd = C.code_id
AND A.del_yn = 'N'
LIMIT ${limit} OFFSET ${offset}

mapper.sql:
/* getBroadcastList-where */ WHERE 1=1
AND A.brdcast_no = #{brdcast_no}
AND A.brdcast_nm ILIKE '%' || #{brdcast_nm} || '%'
AND (A.brdcast_nm ILIKE '%' || #{search_text} || '%' OR B.district_nm ILIKE '%' || #{search_text} || '%' OR maint_sts_nm ILIKE '%' || #{search_text} || '%')

mapper.update:
/* BroadcastMapper.udtBroadcast */ UPDATE tb_broadcast_info
brdcast_nm = #{brdcast_nm}, district_no = #{district_no}, del_yn = #{del_yn}, brdcast_svr_ip = #{brdcast_svr_ip}, brdcast_svr_port = #{brdcast_svr_port}, brdcast_conn_id = #{brdcast_conn_id}, brdcast_conn_pwd = #{brdcast_conn_pwd}, inst_ymd = #{inst_ymd}, maint_sts_cd = #{maint_sts_cd}, brdcast_lon = ${brdcast_lon}, brdcast_lat = ${brdcast_lat}, model_nm = #{model_nm}, brdcast_maker = #{brdcast_maker}, mod_dt = current_timestamp,
WHERE 1 = 1 AND brdcast_no = #{brdcast_no}

mapper.delete:
/* BroadcastMapper.delBroadcast */
DELETE FROM tb_broadcast_info
WHERE 1=1
AND brdcast_no = #{brdcast_no}
