mapper.select:
/* CctvMapper.getCctvList */ SELECT ROW_NUMBER() OVER (ORDER BY A.mod_dt IS NULL ASC, A.mod_dt DESC, A.reg_dt DESC) AS row_num ,A.cctv_no ,A.cctv_nm ,A.district_no ,A.model_nm ,A.cctv_maker ,TO_CHAR(TO_DATE(A.inst_ymd, 'YYYYMMDD'), 'YYYY-MM-DD') AS inst_ymd ,A.cctv_ip ,A.web_port ,A.rtsp_port ,A.cctv_conn_id ,A.cctv_conn_pwd ,A.relay_nm ,A.relay_ip ,A.relay_port ,A.maint_sts_cd ,A.cctv_lon ,A.cctv_lat ,A.admin_center ,A.partner_comp_id ,A.partner_comp_user_nm ,A.partner_comp_user_phone ,TO_CHAR(A.reg_dt, 'YYYY-MM-DD HH24:MI:SS') AS reg_dt ,TO_CHAR(A.mod_dt, 'YYYY-MM-DD HH24:MI:SS') AS mod_dt FROM tb_cctv_info A
AND A.del_yn = 'N'
LIMIT ${limit} OFFSET ${offset}

mapper.sql:
/* getCctvList-where */
WHERE 1=1

	AND A.cctv_no = #{cctv_no}


	AND A.cctv_nm ILIKE '%' || #{cctv_nm} || '%'

mapper.update:
/* CctvMapper.udtCctv */ UPDATE tb_cctv_info
cctv_nm = #{cctv_nm}, district_no = #{district_no}, del_yn = #{del_yn}, model_nm = #{model_nm}, cctv_maker = #{cctv_maker}, inst_ymd = #{inst_ymd}, cctv_ip = #{cctv_ip}, web_port = #{web_port}, rtsp_port = #{rtsp_port}, cctv_conn_id = #{cctv_conn_id}, cctv_conn_pwd = #{cctv_conn_pwd}, relay_nm = #{relay_nm}, relay_ip = #{relay_ip}, relay_port = #{relay_port}, maint_sts_cd = #{maint_sts_cd}, cctv_lon = ${cctv_lon}, cctv_lat = ${cctv_lat}, admin_center = #{admin_center}, partner_comp_id = #{partner_comp_id}, partner_comp_user_nm = #{partner_comp_user_nm}, partner_comp_user_phone = #{partner_comp_user_phone}, mod_dt = current_timestamp,
WHERE 1 = 1 AND cctv_no = #{cctv_no}

mapper.delete:
/* CctvMapper.delCctv */
DELETE FROM tb_cctv_info
WHERE 1=1
AND cctv_no = #{cctv_no}

mapper.update:
/* CctvMapper.incMaxGenerationKey2 */
UPDATE generation_key
SET max = max+1
WHERE table_nm = #{table_nm}
AND column_nm = #{column_nm}

/* CctvMapper.nextPreCodeGenerationKey2 */
UPDATE generation_key
SET pre_code = CHAR(ASCII(pre_code)+1)
WHERE table_nm = #{table_nm}
AND column_nm = #{column_nm}
