mapper.select:
/* DisplayBoardMapper.getDisplayBoardList */ SELECT ROW_NUMBER() OVER (ORDER BY A.mod_dt IS NULL ASC, A.mod_dt DESC, A.reg_dt DESC) AS row_num ,A.dispbd_no ,A.dispbd_nm ,A.district_no ,B.district_nm ,A.dispbd_ip ,A.dispbd_port ,A.dispbd_conn_id ,A.dispbd_conn_pwd ,TO_CHAR(TO_DATE(A.inst_ymd, 'YYYYMMDD'), 'YYYY-MM-DD') AS inst_ymd ,A.maint_sts_cd ,C.code_nm AS maint_sts_nm ,A.dispbd_lat ,A.dispbd_lon ,A.model_nm ,A.dispbd_maker ,TO_CHAR(A.reg_dt, 'YYYY-MM-DD HH24:MI:SS') AS reg_dt ,TO_CHAR(A.mod_dt, 'YYYY-MM-DD HH24:MI:SS') AS mod_dt FROM tb_dispboard_info A
AND A.del_yn = 'N'
LIMIT ${limit} OFFSET ${offset}

mapper.sql:
/* getDisplayBoardList-where */ LEFT OUTER JOIN tb_district_info B ON A.district_no = B.district_no LEFT OUTER JOIN (select * from tb_common_code where code_grp_id = 'MTN000') C on A.maint_sts_cd = C.code_id WHERE 1=1
AND A.dispbd_no = #{dispbd_no}
AND A.dispbd_nm ILIKE '%' || #{dispbd_nm} || '%'
AND (A.dispbd_nm ILIKE '%' || #{search_text} || '%' OR B.district_nm ILIKE '%' || #{search_text} || '%' OR C.code_nm ILIKE '%' || #{search_text} || '%')

mapper.update:
/* DisplayBoardMapper.udtDisplayBoard */ UPDATE tb_dispboard_info
dispbd_nm = #{dispbd_nm}, district_no = #{district_no}, del_yn = #{del_yn}, dispbd_ip = #{dispbd_ip}, dispbd_port = #{dispbd_port}, dispbd_conn_id = #{dispbd_conn_id}, dispbd_conn_pwd = #{dispbd_conn_pwd}, inst_ymd = #{inst_ymd}, maint_sts_cd = #{maint_sts_cd}, dispbd_lon = ${dispbd_lon}, dispbd_lat = ${dispbd_lat}, model_nm = #{model_nm}, dispbd_maker = #{dispbd_maker}, mod_dt = current_timestamp,
WHERE 1 = 1 AND dispbd_no = #{dispbd_no}

mapper.delete:
/* DisplayBoardMapper.delDisplayBoard */
DELETE
FROM tb_dispboard_info
WHERE 1 = 1
  AND dispbd_no = #{dispbd_no}
