mapper.insert:
INSERT INTO tb_logger_info (
    logr_no
   , district_no
   , logr_nm
   , logr_MAC
   , logr_ip
   , logr_port
   , logr_svr_ip
   , logr_svr_port
   , maint_sts_cd
   , logr_lon
   , logr_lat
   , inst_ymd
   , logr_maker
   , model_nm
   , logr_flag
) VALUES ( #{logr_no}
         , #{district_no}
         , #{logr_nm}
         , #{logr_MAC}
         , #{logr_ip}
         , #{logr_port}
         , #{logr_svr_ip}
         , #{logr_svr_port}
         , #{maint_sts_cd}
         , #{logr_lon}::double precision
         , #{logr_lat}::double precision
         , #{inst_ymd}
         , #{logr_maker}
         , #{model_nm}
         , #{logr_flag}
 )

mapper.update:
update tb_logger_info set
    mod_dt = now()

    , district_no = #{district_no}


    , logr_nm = #{logr_nm}


    , logr_MAC = #{logr_MAC}


    , logr_port = #{logr_port}


    , logr_svr_ip = #{logr_svr_ip}


    , logr_svr_port = #{logr_svr_port}


    , maint_sts_cd = #{maint_sts_cd}


    , logr_lon = #{logr_lon}::double precision


    , logr_lat = #{logr_lat}::double precision


    , inst_ymd = #{inst_ymd}


    , logr_maker = #{logr_maker}


    , model_nm = #{model_nm}


    , logr_flag = #{logr_flag}

where logr_no = #{logr_no}
