    #{mapping_no}::int
    , #{sens_no}
    , #{sens_chnl_id}
    , #{logr_no}
    , #{logr_idx_no}
    , #{sens_chnl_nm}
    , #{logr_chnl_seq}::int
    , #{sect_no}::int
    , #{district_no}
)

mapper.update:
