mapper.select:
select send_contr_id, send_group_id, dp_effect, (select name from tb_common_code where code = a.dp_effect::numeric and category='표시효과') as dp_effect_name, dp_time, url_path, filename, send_type, use_yn from tb_send_contr a where send_group_id = #{send_group_id, jdbcType=VARCHAR}
and send_type = #{send_type}
order by reg_date asc

mapper.insert:
insert into tb_send_contr(
    send_contr_id
    , send_group_id
    , dp_effect
    , dp_time
    , url_path
    , filename
    , send_type
    , use_yn
    , reg_date
)
SELECT COALESCE(max(send_contr_id::integer),0)+1 as send_contr_id
     , #{send_group_id, jdbcType=VARCHAR}
     , #{dp_effect, jdbcType=VARCHAR}
     , #{dp_time, jdbcType=VARCHAR}
     , #{url_path, jdbcType=VARCHAR}
     , #{filename, jdbcType=VARCHAR}
     , #{send_type, jdbcType=VARCHAR}
     , 'Y'
     , now()
FROM tb_send_contr
