mapper.insert:
INSERT INTO tb_sensor_info (
sens_no, logr_no, senstype_no, sens_nm, inst_ymd, sect_no, nonrecv_limit_min, alarm_use_yn, sms_snd_yn, sens_disp_yn, maint_sts_cd, sens_lon, sens_lat, sens_maker, model_nm,
) VALUES (
#{sens_no}, #{logr_no}, #{senstype_no}, #{sens_nm}, #{inst_ymd}, #{sect_no}::int, #{nonrecv_limit_min}::int, #{alarm_use_yn}, #{sms_snd_yn}, #{sens_disp_yn}, #{maint_sts_cd}, #{sens_lon}::double precision, #{sens_lat}::double precision, #{sens_maker}, #{model_nm},
)

mapper.update:
update tb_sensor_info set
    mod_dt = now()

    , logr_no = #{logr_no}


    , senstype_no = #{senstype_no}


    , sens_nm = #{sens_nm}


    , inst_ymd = #{inst_ymd}


    , sect_no = #{sect_no}::int


    , multi_sens_yn = #{multi_sens_yn}


    , disp_prior_yn = #{disp_prior_yn}


    , multi_senstype_no = #{multi_senstype_no}


    , multi_sens_no = #{multi_sens_no}


    , nonrecv_limit_min = #{nonrecv_limit_min}::int


    , alarm_use_yn = #{alarm_use_yn}


    , sms_snd_yn = #{sms_snd_yn}


    , sens_disp_yn = #{sens_disp_yn}


    , maint_sts_cd = #{maint_sts_cd}


    , sens_lon = #{sens_lon}::double precision


    , sens_lat = #{sens_lat}::double precision


    , sens_maker = #{sens_maker}


    , model_nm = #{model_nm}

where sens_no = #{sens_no}
