mapper.insert:
insert into tb_sms_details ( mgnt_no
                           , alarm_mgnt_no
                           , sms_trans_dt
                           , sms_subj
                           , sms_msg_dtls
                           , sms_recv_ph
                           , sms_recv_chgr
                           , district_nm
                           , inst_comp_nm
                           , inst_comp_chgr
                           , inst_comp_ph
                           , alarm_lvl_nm
                           , send_tp_nm
                           , inst_sens_cnt
                           , alarm_sens_cnt
                           , nonrecv_sens_cnt
                           , sens_nm
                           , dps_chgr_nm
                           , dps_chgr_ph
                           , mobil_link_url
                           , sms_rslt_yn)
values ( (SELECT COALESCE(max(mgnt_no::integer), 0) + 1 from tb_sms_details)
       , #{alarm_mgnt_no}
       , now()
       , #{sms_subj}
       , #{sms_msg_dtls}
       , #{sms_recv_ph}
       , #{sms_recv_chgr}
       , #{district_nm}
       , #{inst_comp_nm}
       , #{inst_comp_chgr}
       , #{inst_comp_ph}
       , #{alarm_lvl_nm}
       , #{send_tp_nm}
       , #{inst_sens_cnt}
       , #{alarm_sens_cnt}
       , #{nonrecv_sens_cnt}
       , #{sens_nm}
       , #{dps_chgr_nm}
       , #{dps_chgr_ph}
       , #{mobil_link_url}
       , #{sms_rslt_yn})
