mapper.insert:
INSERT INTO tb_sms_receiver ( mgnt_no
                            , district_no
                            , sms_recv_dept
                            , sms_chgr_nm
                            , sms_recv_ph
                            , alarm_lvl_nm
                            , sms_autosnd_yn
                            , reg_dt
                            , mod_dt
                            , partner_comp_id)
VALUES ( (SELECT COALESCE(MAX(mgnt_no), 0) + 1 FROM tb_sms_receiver)
       , #{district_no}
       , #{sms_recv_dept}
       , #{sms_chgr_nm}
       , #{sms_recv_ph}
       , #{alarm_lvl_nm}
       , #{sms_autosnd_yn}
       , now()
       , now()
       , #{partner_comp_id})

mapper.update:
UPDATE tb_sms_receiver
SET alarm_lvl_nm   = #{alarm_lvl_nm}
  , sms_autosnd_yn = #{sms_autosnd_yn}
  , mod_dt         = now()
WHERE mgnt_no = #{mgnt_no}
