mapper.insert:
INSERT INTO tb_alarm ( alarm_uid
                     , asset_id
                     , asset_kind_id
                     , alarm_kind_id
                     , reg_date
                     , description
                     , risk_level
                     , alarm_type
                     , alarm_title
                     , alarm_desc
                     , view_flag)
SELECT COALESCE(max(alarm_uid::integer), 0) + 1 as alarm_uid
     , #{asset_id}
     , #{asset_kind_id}
     , #{alarm_kind_id}
     , now()
     , #{description}
     , #{risk_level}
     , #{type}
     , #{name}
     , #{description}
     , 'Y'
FROM tb_alarm
