mapper.select:
SELECT TO_CHAR(ROUND(tsi.init_raw_data::numeric, 2), 'FM9999999999999999990.00') AS formul_data, tsi.init_apply_dt, tst.sens_tp_nm, tsi.last_apply_dt, tsi.emer_status, COALESCE(tsi.sens_chnl_id, '') AS sens_chnl_id, tsi.sens_chnl_id AS og_sens_chnl_id, tsi.sens_no, tsinfo.sens_nm, TO_CHAR(ROUND(tmd.raw_data::numeric, 2), 'FM9999999999999999990.00') AS real_data, dist.district_nm, tcc.code_nm AS sens_status FROM tb_sensor_init tsi LEFT JOIN tb_sensor_info tsinfo ON tsi.sens_no = tsinfo.sens_no LEFT JOIN tb_sensor_type tst ON tsinfo.senstype_no = tst.senstype_no LEFT JOIN tb_common_code tcc ON tsinfo.maint_sts_cd = tcc.code_id
LEFT JOIN LATERAL ( SELECT tdi.district_nm FROM tb_logr_idx_map tlim INNER JOIN tb_logger_info tli ON tlim.logr_no = tli.logr_no INNER JOIN tb_district_info tdi ON tli.district_no = tdi.district_no WHERE tlim.sens_no = tsi.sens_no LIMIT 1 ) dist ON TRUE LEFT JOIN LATERAL ( SELECT sens_no, formul_data,raw_data, sens_chnl_id FROM tb_measure_details tmd2 WHERE tmd2.sens_no = tsi.sens_no AND tmd2.sens_chnl_id = tsi.sens_chnl_id ORDER BY tmd2.meas_dt DESC LIMIT 1 ) tmd ON TRUE
WHERE 1 = 1 AND tsi.sens_no IN ( SELECT tlim.sens_no FROM tb_logr_idx_map tlim INNER JOIN tb_logger_info tli ON tlim.logr_no = tli.logr_no WHERE 1=1
AND tli.district_no = #{district_no}
)
limit #{rows}::int offset (#{page}::int - 1) * #{rows}::int

select count(tsi.sens_no) from tb_sensor_init tsi left join tb_sensor_info tsinfo on tsi.sens_no = tsinfo.sens_no left join tb_sensor_type tst on tsinfo.senstype_no = tst.senstype_no left join tb_common_code tcc on tsinfo.maint_sts_cd = tcc.code_id left join lateral ( select tdi.district_nm from tb_logr_idx_map tlim inner join tb_logger_info tli on tlim.logr_no = tli.logr_no inner join tb_district_info tdi on tli.district_no = tdi.district_no where tlim.sens_no = tsi.sens_no limit 1 ) dist on true left join lateral ( select sens_no, formul_data, sens_chnl_id from tb_measure_details tmd2 where tmd2.sens_no = tsi.sens_no and tmd2.sens_chnl_id = tsi.sens_chnl_id order by tmd2.meas_dt desc limit 1 ) tmd on true
WHERE 1 = 1 AND tsi.sens_no IN ( SELECT tlim.sens_no FROM tb_logr_idx_map tlim INNER JOIN tb_logger_info tli ON tlim.logr_no = tli.logr_no WHERE 1=1
AND tli.district_no = #{district_no}
)

mapper.update:
UPDATE tb_sensor_init
SET

    init_raw_data = #{formul_data}::float ,

emer_status = #{emer_status}
, init_apply_dt = now()
, mod_dt = now()
WHERE sens_no = #{sens_no}
and sens_chnl_id = #{og_sens_chnl_id}
